class Airplane:
    def get_airplanes(date=datetime.datetime.today().strftime('%Y-%m-%d'), lang="en", arrival="false"):
        params = {
            "date": date,
            "lang": lang,
            "cargo": "false",
            "arrival": arrival
        }
        response = requests.get("https://www.hongkongairport.com/flightinfo-rest/rest/flights/past", params=params)
        for i in response.json():
            if params["date"] == i["date"]:
                return i["list"]


class WeatherData(models.Model):
    date = models.DateField(auto_now=False, primary_key=True)
    minTemp = models.IntegerField()
    maxTemp = models.IntegerField()
    weather_type = models.CharField(max_length=30)
    rain_probability = models.IntegerField()


class BoardAuthor(models.Model):
    username = models.CharField(max_length=150, null=False, blank=False)

    def __str__(self):
        return self.username

class WeatherBoard(models.Model):
    author = models.ForeignKey(BoardAuthor, on_delete=models.CASCADE)
    shatin = models.JSONField(name='care')

        